Get-ADComputer -Filter * -Properties * | Where-Object { ($_.DistinguishedName -notlike "*OU=Workstations*") -AND ($_.DistinguishedName -notlike "*OU=Servers*") -AND ($_.DistinguishedName -notlike "*OU=Apple MAC*") -AND ($_.DistinguishedName -notlike "*OU=Terminales Fabrica*") -AND ($_.DistinguishedName -notlike "*OU=Domain Controllers*") -AND ($_.DistinguishedName -notlike "*OU=-101 COMPUTERS DISABLED*")} | Select-Object Name, Enabled, LastLogonDate, DistinguishedName, operatingSystem | Export-Csv -Path "C:\ts_data\Scripts AD\Ou_INC.csv" -Delimiter ";" -Encoding UTF8